import { useState, useCallback } from "react";
import "./App.css";

const useValidatedInput = (initialValue, validator) => {
  const [value, setValue] = useState(initialValue);
  const [error, setError] = useState("");

  const handleChange = useCallback(
    (newValue) => {
      setValue(newValue);
      const validationError = validator(newValue);
      setError(validationError);
    },
    [validator]
  );

  return [value, handleChange, error];
};

const validators = {
  amount: (value) => {
    if (value <= 0) return "Amount must be greater than 0";
    if (value > 10000000) return "Amount cannot exceed 10,000,000";
    return "";
  },
  rate: (value) => {
    if (value < 0) return "Interest rate cannot be negative";
    if (value > 100) return "Interest rate cannot exceed 100%";
    return "";
  },
  period: (value) => {
    if (value <= 0) return "Period must be greater than 0";
    if (value > 1200) return "Period cannot exceed 1200 months (100 years)";
    return "";
  },
};

const calculateSimpleInterest = (principal, monthlyRate, months) => {
  return principal * (1 + (monthlyRate / 100) * months);
};

const calculateCompoundInterest = (
  principal,
  monthlyRate,
  months,
  frequency
) => {
  const annualRate = (monthlyRate / 100) * 12;
  const compoundingFrequency = frequency === "monthly" ? 12 : 1;
  const years = months / 12;

  return (
    principal *
    Math.pow(
      1 + annualRate / compoundingFrequency,
      compoundingFrequency * years
    )
  );
};

const InputField = ({
  label,
  type = "number",
  value,
  onChange,
  error,
  placeholder,
  min,
  max,
  step = "any",
}) => (
  <div className="input-group">
    <label className="input-label">{label}</label>
    <input
      type={type}
      value={value}
      onChange={(e) =>
        onChange(type === "number" ? Number(e.target.value) : e.target.value)
      }
      className={`input-field ${error ? "input-error" : ""}`}
      placeholder={placeholder}
      min={min}
      max={max}
      step={step}
      aria-describedby={
        error ? `${label.replace(/\s+/g, "-").toLowerCase()}-error` : undefined
      }
    />
    {error && (
      <span
        id={`${label.replace(/\s+/g, "-").toLowerCase()}-error`}
        className="error-message"
        role="alert"
      >
        {error}
      </span>
    )}
  </div>
);

const SelectField = ({ label, value, onChange, options, disabled = false }) => (
  <div className="input-group">
    <label className="input-label">{label}</label>
    <select
      value={value}
      onChange={(e) => onChange(e.target.value)}
      className="select-field"
      disabled={disabled}
    >
      {options.map((option) => (
        <option key={option.value} value={option.value}>
          {option.label}
        </option>
      ))}
    </select>
  </div>
);

export default function InterestCalculator() {
  const [amount, setAmount, amountError] = useValidatedInput(
    0,
    validators.amount
  );
  const [monthlyRate, setMonthlyRate, rateError] = useValidatedInput(
    0,
    validators.rate
  );
  const [period, setPeriod, periodError] = useValidatedInput(
    0,
    validators.period
  );
  const [interestType, setInterestType] = useState("simple");
  const [compoundFreq, setCompoundFreq] = useState("monthly");
  const [result, setResult] = useState(null);
  const [isCalculating, setIsCalculating] = useState(false);

  const hasErrors = amountError || rateError || periodError;
  const hasEmptyFields = !amount || !monthlyRate || !period;

  const calculateInterest = useCallback(async () => {
    if (hasErrors || hasEmptyFields) return;

    setIsCalculating(true);

    await new Promise((resolve) => setTimeout(resolve, 300));

    try {
      let finalAmount;

      if (interestType === "simple") {
        finalAmount = calculateSimpleInterest(amount, monthlyRate, period);
      } else {
        finalAmount = calculateCompoundInterest(
          amount,
          monthlyRate,
          period,
          compoundFreq
        );
      }

      setResult(Math.round(finalAmount * 100) / 100);
    } catch (error) {
      console.error("Calculation error:", error);
      setResult(null);
    } finally {
      setIsCalculating(false);
    }
  }, [
    amount,
    monthlyRate,
    period,
    interestType,
    compoundFreq,
    hasErrors,
    hasEmptyFields,
  ]);

  const resetCalculator = () => {
    setAmount(0);
    setMonthlyRate(0);
    setPeriod(0);
    setInterestType("simple");
    setCompoundFreq("monthly");
    setResult(null);
  };

  const interestTypeOptions = [
    { value: "simple", label: "Simple Interest" },
    { value: "compound", label: "Compound Interest" },
  ];

  const compoundFreqOptions = [
    { value: "monthly", label: "Monthly" },
    { value: "yearly", label: "Yearly" },
  ];

  return (
    <div className="calculator-container">
      <header className="calculator-header">
        <h1>Interest Calculator</h1>
        <p>Calculate simple and compound interest with ease</p>
      </header>

      <main className="calculator-form">
        <InputField
          label="Principal Amount (₹)"
          value={amount}
          onChange={setAmount}
          error={amountError}
          placeholder="Enter amount"
          min="0"
          max="10000000"
        />

        <InputField
          label="Monthly Interest Rate (%)"
          value={monthlyRate}
          onChange={setMonthlyRate}
          error={rateError}
          placeholder="Enter monthly rate"
          min="0"
          max="100"
          step="0.01"
        />

        <InputField
          label="Time Period (months)"
          value={period}
          onChange={setPeriod}
          error={periodError}
          placeholder="Enter period in months"
          min="0"
          max="1200"
        />

        <SelectField
          label="Interest Type"
          value={interestType}
          onChange={setInterestType}
          options={interestTypeOptions}
        />

        {interestType === "compound" && (
          <SelectField
            label="Compounding Frequency"
            value={compoundFreq}
            onChange={setCompoundFreq}
            options={compoundFreqOptions}
          />
        )}

        <div className="button-group">
          <button
            onClick={calculateInterest}
            disabled={hasErrors || hasEmptyFields || isCalculating}
            className="calculate-button"
            aria-describedby="calculation-status"
          >
            {isCalculating ? "Calculating..." : "Calculate Interest"}
          </button>

          <button
            onClick={resetCalculator}
            className="reset-button"
            type="button"
          >
            Reset
          </button>
        </div>

        {result !== null && (
          <div
            className="result-container"
            role="region"
            aria-label="Calculation Result"
          >
            <h2>Calculation Result</h2>
            <div className="result-details">
              <div className="result-item">
                <span className="result-label">Principal Amount:</span>
                <span className="result-value">
                  ₹{amount.toLocaleString("en-IN")}
                </span>
              </div>
              <div className="result-item">
                <span className="result-label">Interest Earned:</span>
                <span className="result-value">
                  ₹{(result - amount).toLocaleString("en-IN")}
                </span>
              </div>
              <div className="result-item final-amount">
                <span className="result-label">Final Amount:</span>
                <span className="result-value">
                  ₹{result.toLocaleString("en-IN")}
                </span>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}
