import React, { useState } from 'react';

const DateRangePicker = ({ 
  label, 
  startDate, 
  endDate, 
  onDateChange, 
  error,
  required = false 
}) => {
  const [isOpen, setIsOpen] = useState(false);

  const calculatePeriod = (start, end) => {
    if (!start || !end) return 0;
    const startDate = new Date(start);
    const endDate = new Date(end);
    const diffTime = Math.abs(endDate - startDate);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return Math.ceil(diffDays / 30); // Convert to months
  };

  const handleStartDateChange = (date) => {
    const period = calculatePeriod(date, endDate);
    onDateChange(date, endDate, period);
  };

  const handleEndDateChange = (date) => {
    const period = calculatePeriod(startDate, date);
    onDateChange(startDate, date, period);
  };

  const formatDateRange = () => {
    if (!startDate || !endDate) return 'Select date range';
    const start = new Date(startDate).toLocaleDateString('en-IN');
    const end = new Date(endDate).toLocaleDateString('en-IN');
    const months = calculatePeriod(startDate, endDate);
    return `${start} to ${end} (${months} months)`;
  };

  return (
    <div className="input-group">
      <label className="input-label">
        {label}
        {required && <span className="required-indicator">*</span>}
      </label>

      <div className="date-range-container">
        <button
          type="button"
          className={`date-range-button ${error ? 'input-error' : ''}`}
          onClick={() => setIsOpen(!isOpen)}
        >
          <span className="date-range-text">{formatDateRange()}</span>
          <span className="date-range-icon">ðŸ“…</span>
        </button>

        {isOpen && (
          <div className="date-range-popup">
            <div className="date-input-group">
              <label>Start Date:</label>
              <input
                type="date"
                value={startDate}
                onChange={(e) => handleStartDateChange(e.target.value)}
                className="date-input"
              />
            </div>
            <div className="date-input-group">
              <label>End Date:</label>
              <input
                type="date"
                value={endDate}
                onChange={(e) => handleEndDateChange(e.target.value)}
                min={startDate}
                className="date-input"
              />
            </div>
            <button
              type="button"
              className="date-range-close"
              onClick={() => setIsOpen(false)}
            >
              Done
            </button>
          </div>
        )}
      </div>

      {error && (
        <span className="error-message" role="alert">
          {error}
        </span>
      )}
    </div>
  );
};

export default DateRangePicker;
